// Redirects the user after 15 seconds based on a standard set of ids. 
// For use with errors generated by 'general-redirect-error.pug'
const startRedirect = () => {
    // Loads the element containing the the text time until the redirect into a variable.
    const timeElement = document.getElementById('redirectTime');
    // Passes the text number into an integer.
    let time = parseInt(timeElement.textContent);
    // Defines a sub-function so that the code can be reused as a form of loop.
    const wait = () => {
        // Waits 1 second.
        setTimeout(()=> {
            // Tests if the timer has expired yet.
            if (time > 0) {
                // If the timer is still running, the time left is reduced by 1 second and shown on screen.
                time = time-1;
                timeElement.textContent = time;
                // The 'wait' function is called again.
                wait();
            } else {
                // If the timer value is 0, the redirect link is clicked.
                document.getElementById('redirectAddress').click();
            }
        }, 1000);
    };
    // The wait function is called for the first time to initiate the whole process.
    wait();
};